function Assessments_js() {
    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilegrid_11': 'Assessments_mobilegrid_11',
        'mobilegridcell_12': 'Assessments_mobilegridcell_12',
        'mobilebutton_10': 'Assessments_mobilebutton_10',
        'mobilegridcell_13': 'Assessments_mobilegridcell_13',
        'mobilelabel_16': 'Assessments_mobilelabel_16',
        'mobilegrid_4': 'Assessments_mobilegrid_4',
        'mobilegridcell_5': 'Assessments_mobilegridcell_5',
        'mobilelabel_17': 'Assessments_mobilelabel_17',
        'subject_select': 'Assessments_subject_select',
        'subject_select-0': 'Assessments_subject_select-0',
        'mobilegridcell_7': 'Assessments_mobilegridcell_7',
        'grade_select': 'Assessments_grade_select',
        'grade_select-0': 'Assessments_grade_select-0',
        'mobilebutton_9': 'Assessments_mobilebutton_9'
    };
    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }
    /*
     * Nonvisual components
     */
    Apperyio.mappings = Apperyio.mappings || {};
    Apperyio.datasources = Apperyio.datasources || {};
    Apperyio.CurrentScreen = 'Assessments';
    _.chain(Apperyio.mappings)
        .filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        })
        .each(Apperyio.UIHandler.hideTemplateComponents);
    /*
     * Events and handlers
     */
    // On Load
    var Assessments_onLoad = function() {
        Assessments_elementsExtraJS();
        $('div[dsid="mobilecontainer"]').css('background-image',
            'url(' + Appery.getImagePath('Artboard_1.png') + ')');
        $('div[dsid="mobilecontainer"]').css('background-size', '100% auto');
        $('div[dsid="mobilecontainer"]').css('background-color', '#ffffff');
        $('div[dsid="mobilecontainer"]').css('background-position', 'center');
        $('div[dsid="mobilecontainer"]').css('background-repeat', 'no-repeat');
        $('div[dsid="mobilecontainer"]').css('background-position', 'top');
        Apperyio("grade_select").parent().css("background", "#ffffff");
        Apperyio("grade_select").parent().css("border-width", "1px");
        Apperyio("grade_select").parent().css("border-radius", "40px");
        Apperyio("grade_select").parent().css("border-color", "#ff725e");
        Apperyio("grade_select").parent().css("color", "#ff725e");
        Apperyio("subject_select").parent().css("background", "#ffffff");
        Apperyio("subject_select").parent().css("border-width", "1px");
        Apperyio("subject_select").parent().css("border-radius", "40px");
        Apperyio("subject_select").parent().css("border-color", "#ff725e");
        Apperyio("subject_select").parent().css("color", "#ff725e");
        Assessments_deviceEvents();
        Assessments_windowEvents();
        Assessments_elementsEvents();
    };
    // screen window events
    function Assessments_windowEvents() {
        $('#Assessments').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#Assessments').on({
            pageshow: function(event) {
                var myProfile = Apperyio.storage.myProfile.get();
                if (myProfile.accountType === 'Student/Parent' ||
                    myProfile.accountType === 'Teacher' ||
                    myProfile.accountType === 'Just Curious') {
                    Apperyio("grade_select").val(myProfile.grade).trigger("change")
                    $(".Assessments_grade_select").hide();
                } else {
                    $(".Assessments_grade_select").show();
                };
            },
        });
    };
    // device events
    function Assessments_deviceEvents() {
        document.addEventListener("deviceready", function() {
        });
    };
    // screen elements extra js
    function Assessments_elementsExtraJS() {
        // screen (Assessments) extra code
        /* subject_select */
        $("#Assessments_subject_select").parent().find("a.ui-btn").attr("tabindex", "3");
        /* grade_select */
        $("#Assessments_grade_select").parent().find("a.ui-btn").attr("tabindex", "4");
    };
    // screen elements handler
    function Assessments_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });
        $(document).off("click", '#Assessments_mobilecontainer [name="mobilebutton_10"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Home', {
                        transition: 'slide',
                        reverse: true
                    });
                }
            },
        }, '#Assessments_mobilecontainer [name="mobilebutton_10"]');
        $(document).off("click", '#Assessments_mobilecontainer [name="mobilebutton_9"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    try {
                        $a.storage["selectedSubject"].update("$", $a.c15r($a.UIHandler.resolveGeneratedComponent('subject_select', this), 'get', 'selectedMenuItem'))
                    } catch (e) {
                        console.error(e)
                    };
                    try {
                        $a.storage["selectedGrade"].update("$", $a.c15r($a.UIHandler.resolveGeneratedComponent('grade_select', this), 'get', 'selectedMenuItem'))
                    } catch (e) {
                        console.error(e)
                    };
                    Apperyio.navigateTo('Assessment', {
                        reverse: false
                    });
                }
            },
        }, '#Assessments_mobilecontainer [name="mobilebutton_9"]');
    };
    $(document).off("pagebeforeshow", "#Assessments").on("pagebeforeshow", "#Assessments", function(event, ui) {
        Apperyio.CurrentScreen = "Assessments";
        _.chain(Apperyio.mappings)
            .filter(function(m) {
                return m.homeScreen === Apperyio.CurrentScreen;
            })
            .each(Apperyio.UIHandler.hideTemplateComponents);
    });
    Assessments_onLoad();
};
$(document).off("pagecreate", "#Assessments").on("pagecreate", "#Assessments", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Assessments_js();
});