function register_js() {
    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_17': 'register_mobilebutton_17',
        'mobilelabel_15': 'register_mobilelabel_15',
        'mobilegrid_3': 'register_mobilegrid_3',
        'mobilegridcell_4': 'register_mobilegridcell_4',
        'username_reg': 'register_username_reg',
        'mobilelabel_13': 'register_mobilelabel_13',
        'mobilegridcell_6': 'register_mobilegridcell_6',
        'pass_reg': 'register_pass_reg',
        'mobilelabel_14': 'register_mobilelabel_14',
        'mobilebutton_12': 'register_mobilebutton_12'
    };
    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }
    /*
     * Nonvisual components
     */
    Apperyio.mappings = Apperyio.mappings || {};
    Apperyio.mappings["register_register_onbeforesend_mapping_0"] = {
        "homeScreen": "register",
        "directions": [
            {
                "from_name": "register",
                "from_type": "UI",
                "to_name": "register",
                "to_type": "SERVICE_REQUEST",
                "to_default": {
                    "headers": {
                        "X-Appery-Database-Id": "{database_id}",
                        "Content-Type": "application/json"
                    },
                    "parameters": {},
                    "body": null
                },
                "mappings": [
                    {
                        "source": "$['username_reg:text']",
                        "target": "$['body']['username']"
                    },
                    {
                        "source": "$['pass_reg:text']",
                        "target": "$['body']['password']"
                    }
                ]
            }
        ]
    };
    Apperyio.mappings["register_register_onsuccess_mapping_0"] = {
        "homeScreen": "register",
        "directions": [
            {
                "from_name": "register",
                "from_type": "SERVICE_RESPONSE",
                "to_name": "myUser_id",
                "to_type": "LOCAL_STORAGE",
                "mappings": [
                    {
                        "source": "$['body']['_id']",
                        "target": "$"
                    }
                ]
            },
            {
                "from_name": "register",
                "from_type": "SERVICE_RESPONSE",
                "to_name": "sessionToken",
                "to_type": "LOCAL_STORAGE",
                "mappings": [
                    {
                        "source": "$['body']['sessionToken']",
                        "target": "$"
                    }
                ]
            }
        ]
    };
    Apperyio.datasources = Apperyio.datasources || {};
    window.register = Apperyio.datasources.register = new Apperyio.DataSource(stemguyana_signup_service, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["register_register_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["register_register_onsuccess_mapping_0"]);
            Apperyio.navigateTo('registerProfile', {
                reverse: false
            });
        },
        "onError": function(jqXHR, textStatus, errorThrown) {
            alert("This username may already be registered");;
        }
    });
    Apperyio.CurrentScreen = 'register';
    _.chain(Apperyio.mappings)
        .filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        })
        .each(Apperyio.UIHandler.hideTemplateComponents);
    /*
     * Events and handlers
     */
    // On Load
    var register_onLoad = function() {
        register_elementsExtraJS();
        $('div[dsid="mobilecontainer"]').css('background-image',
            'url(' + Appery.getImagePath('Artboard_1.png') + ')');
        $('div[dsid="mobilecontainer"]').css('background-size', '100% 100%');
        $('div[dsid="mobilecontainer"]').css('background-position', 'center');
        $('div[dsid="mobilecontainer"]').css('background-repeat', 'no-repeat');
        Apperyio("username_reg").parent().css("background", "#ffffff");
        Apperyio("username_reg").parent().css("border-color", "#ff725e");
        Apperyio("username_reg").parent().css("border-width", "1px");
        Apperyio("username_reg").parent().css("color", "#ff725e");
        Apperyio("username_reg").parent().css("font-size", "14px");
        Apperyio("username_reg").parent().css("border-radius", "40px");
        Apperyio("username_reg").parent().css("padding-top", "5px");
        Apperyio("username_reg").parent().css("padding-bottom", "5px");
        Apperyio("username_reg").parent().css("padding-left", "10px");
        Apperyio("pass_reg").parent().css("background", "#ffffff");
        Apperyio("pass_reg").parent().css("border-color", "#ff725e");
        Apperyio("pass_reg").parent().css("border-width", "1px");
        Apperyio("pass_reg").parent().css("color", "#ff725e");
        Apperyio("pass_reg").parent().css("font-size", "14px");
        Apperyio("pass_reg").parent().css("border-radius", "40px");
        Apperyio("pass_reg").parent().css("padding-top", "5px");
        Apperyio("pass_reg").parent().css("padding-bottom", "5px");
        Apperyio("pass_reg").parent().css("padding-left", "10px");;
        register_deviceEvents();
        register_windowEvents();
        register_elementsEvents();
    };
    // screen window events
    function register_windowEvents() {
        $('#register').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
    };
    // device events
    function register_deviceEvents() {
        document.addEventListener("deviceready", function() {
        });
    };
    // screen elements extra js
    function register_elementsExtraJS() {
        // screen (register) extra code
    };
    // screen elements handler
    function register_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });
        $(document).off("click", '#register_mobilecontainer [name="mobilebutton_17"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    $.mobile.back();
                }
            },
        }, '#register_mobilecontainer [name="mobilebutton_17"]');
        $(document).off("click", '#register_mobilecontainer [name="mobilebutton_12"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    try {
                        register.execute({});
                    } catch (e) {
                        console.error(e);
                        hideSpinner();
                    };
                }
            },
        }, '#register_mobilecontainer [name="mobilebutton_12"]');
    };
    $(document).off("pagebeforeshow", "#register").on("pagebeforeshow", "#register", function(event, ui) {
        Apperyio.CurrentScreen = "register";
        _.chain(Apperyio.mappings)
            .filter(function(m) {
                return m.homeScreen === Apperyio.CurrentScreen;
            })
            .each(Apperyio.UIHandler.hideTemplateComponents);
    });
    register_onLoad();
};
$(document).off("pagecreate", "#register").on("pagecreate", "#register", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    register_js();
});